UPDATE `hisdb`.`seg_rep_templates_registry` SET `rep_name` = 'PHIC Accreditation' WHERE `report_id` = 'Billing_PHIC_Accreditation'; 

UPDATE `seg_rep_params` SET `choices` = 'SELECT \'all\' AS id, \'All\' AS namedesc\r\nUNION\r\nSELECT \r\n cp.`parent_dept_nr` AS id ,\r\n fn_get_department_name(cp.`parent_dept_nr`)  AS namedesc\r\nFROM\r\n  care_department AS cp \r\nWHERE cp.`parent_dept_nr` != 0 \r\n  AND cp.`type` = 1 AND cp.`is_inactive` = 0 \r\nGROUP BY cp.`parent_dept_nr` ORDER BY namedesc' WHERE `param_id` = 'major_department'; 
