INSERT INTO `seg_rep_templates_registry` (`report_id`, `rep_group`, `rep_name`, `rep_description`, `rep_script`, `rep_dept_nr`, `rep_category`, `with_template`, `template_name`) VALUES ('Billing_PHIC_Accreditation', 'Hospital Operations', 'PHIC Accreditation with Expiry Date', 'PHIC Accreditation with Expiry Date', 'Billing_PHIC_Accreditation', '152', 'HOSP', '1', 'Billing_PHIC_Accreditation'); 
INSERT INTO `seg_rep_params` (`param_id`, `parameter`, `param_type`, `choices`) VALUES ('major_department', 'Major Department', 'sql', 'SELECT \r\n cp.`parent_dept_nr` AS id ,\r\n fn_get_department_name(cp.`parent_dept_nr`)  AS namedesc\r\nFROM\r\n  care_department AS cp \r\nWHERE cp.`parent_dept_nr` != 0 \r\n  AND cp.`type` = 1 AND cp.`is_inactive` = 0 \r\nGROUP BY cp.`parent_dept_nr` ORDER BY namedesc'); 
INSERT INTO `seg_rep_template_params` (`report_id`, `param_id`) VALUES ('Billing_PHIC_Accreditation', 'major_department'); 
